CREATE VIEW TEMP14 AS
SELECT DISTINCT I.CATEGORY_BIG, I.CATEGORY_SMALL
FROM ITEMS I, TEMP12 T
WHERE T.P_ID = I.PID;

CREATE VIEW TEMP15 AS
SELECT DISTINCT I.PID
FROM ITEMS I, TEMP14 T
WHERE T.CATEGORY_BIG = I.CATEGORY_BIG
AND T.CATEGORY_SMALL = I.CATEGORY_SMALL;

CREATE VIEW TEMP16 AS
SELECT DISTINCT I.C_ID
FROM TEMP15 T, ORDER_DETAIL I
WHERE T.PID = I.P_ID;

SELECT *
FROM TEMP16 T, CUSTOMER C
WHERE T.C_ID = C.ID;

